import { useInjectButtonGroup } from "../../composables/useButtonGroup";
import appConfig from "#build/app.config";
import { input } from "#ui/ui.config";
const config = mergeConfig(appConfig.ui.strategy, appConfig.ui.input, input);
export default defineComponent({
  components: {
    UIcon
  },
  inheritAttrs: false,
  props: {
    modelValue: {
      type: [String, Number],
      default: ""
    },
    type: {
      type: String,
      default: "text"
    },
    id: {
      type: String,
      default: null
    },
    name: {
      type: String,
      default: null
    },
    placeholder: {
      type: String,
      default: null
    },
    required: {
      type: Boolean,
      default: false
    },
    disabled: {
      type: Boolean,
      default: false
    },
    autofocus: {
      type: Boolean,
      default: false
    },
    autofocusDelay: {
      type: Number,
      default: 100
    },
    icon: {
      type: String,
      default: null
    },
    loadingIcon: {
      type: String,
      default: () => config.default.loadingIcon
    },
    leadingIcon: {
      type: String,
      default: null
    },
    trailingIcon: {
      type: String,
      default: null
    },
    trailing: {
      type: Boolean,
      default: false
    },
    leading: {
      type: Boolean,
      default: false
    },
    loading: {
      type: Boolean,
      default: false
    },
    padded: {
      type: Boolean,
      default: true
    },
    size: {
      type: String,
      default: null,
      validator(value) {
        return Object.keys(config.size).includes(value);
      }
    },
    color: {
      type: String,
      default: () => config.default.color,
      validator(value) {
        return [...appConfig.ui.colors, ...Object.keys(config.color)].includes(value);
      }
    },
    variant: {
      type: String,
      default: () => config.default.variant,
      validator(value) {
        return [
          ...Object.keys(config.variant),
          ...Object.values(config.color).flatMap((value2) => Object.keys(value2))
        ].includes(value);
      }
    },
    inputClass: {
      type: String,
      default: null
    },
    class: {
      type: [String, Object, Array],
      default: () => ""
    },
    ui: {
      type: Object,
      default: () => ({})
    },
    modelModifiers: {
      type: Object,
      default: () => ({})
    }
  },
  emits: ["update:modelValue", "blur", "change"],
  setup(props, { emit, slots }) {
    const { ui, attrs } = useUI("input", toRef(props, "ui"), config, toRef(props, "class"));
    const { size: sizeButtonGroup, rounded } = useInjectButtonGroup({ ui, props });
    const { emitFormBlur, emitFormInput, size: sizeFormGroup, color, inputId, name } = useFormGroup(props, config);
    const size = computed(() => sizeButtonGroup.value ?? sizeFormGroup.value);
    const modelModifiers = ref(defu({}, props.modelModifiers, { trim: false, lazy: false, number: false, nullify: false }));
    const input2 = ref(null);
    const autoFocus = () => {
      if (props.autofocus) {
        input2.value?.focus();
      }
    };
    const updateInput = (value) => {
      if (modelModifiers.value.trim) {
        value = value.trim();
      }
      if (modelModifiers.value.number || props.type === "number") {
        value = looseToNumber(value);
      }
      if (modelModifiers.value.nullify) {
        value ||= null;
      }
      emit("update:modelValue", value);
      emitFormInput();
    };
    const onInput = (event) => {
      if (!modelModifiers.value.lazy) {
        updateInput(event.target.value);
      }
    };
    const onChange = (event) => {
      if (props.type === "file") {
        const value = event.target.files;
        emit("change", value);
      } else {
        const value = event.target.value;
        emit("change", value);
        if (modelModifiers.value.lazy) {
          updateInput(value);
        }
        if (modelModifiers.value.trim) {
          event.target.value = value.trim();
        }
      }
    };
    const onBlur = (event) => {
      emitFormBlur();
      emit("blur", event);
    };
    onMounted(() => {
      setTimeout(() => {
        autoFocus();
      }, props.autofocusDelay);
    });
    const inputClass = computed(() => {
      const variant = ui.value.color?.[color.value]?.[props.variant] || ui.value.variant[props.variant];
      return twMerge(twJoin(
        ui.value.base,
        ui.value.form,
        rounded.value,
        ui.value.placeholder,
        props.type === "file" && ui.value.file.base,
        ui.value.size[size.value],
        props.padded ? ui.value.padding[size.value] : "p-0",
        variant?.replaceAll("{color}", color.value),
        (isLeading.value || slots.leading) && ui.value.leading.padding[size.value],
        (isTrailing.value || slots.trailing) && ui.value.trailing.padding[size.value]
      ), props.inputClass);
    });
    const isLeading = computed(() => {
      return props.icon && props.leading || props.icon && !props.trailing || props.loading && !props.trailing || props.leadingIcon;
    });
    const isTrailing = computed(() => {
      return props.icon && props.trailing || props.loading && props.trailing || props.trailingIcon;
    });
    const leadingIconName = computed(() => {
      if (props.loading) {
        return props.loadingIcon;
      }
      return props.leadingIcon || props.icon;
    });
    const trailingIconName = computed(() => {
      if (props.loading && !isLeading.value) {
        return props.loadingIcon;
      }
      return props.trailingIcon || props.icon;
    });
    const leadingWrapperIconClass = computed(() => {
      return twJoin(
        ui.value.icon.leading.wrapper,
        ui.value.icon.leading.pointer,
        ui.value.icon.leading.padding[size.value]
      );
    });
    const leadingIconClass = computed(() => {
      return twJoin(
        ui.value.icon.base,
        color.value && appConfig.ui.colors.includes(color.value) && ui.value.icon.color.replaceAll("{color}", color.value),
        ui.value.icon.size[size.value],
        props.loading && ui.value.icon.loading
      );
    });
    const trailingWrapperIconClass = computed(() => {
      return twJoin(
        ui.value.icon.trailing.wrapper,
        ui.value.icon.trailing.pointer,
        ui.value.icon.trailing.padding[size.value]
      );
    });
    const trailingIconClass = computed(() => {
      return twJoin(
        ui.value.icon.base,
        color.value && appConfig.ui.colors.includes(color.value) && ui.value.icon.color.replaceAll("{color}", color.value),
        ui.value.icon.size[size.value],
        props.loading && !isLeading.value && ui.value.icon.loading
      );
    });
    return {
      // eslint-disable-next-line vue/no-dupe-keys
      ui,
      attrs,
      // eslint-disable-next-line vue/no-dupe-keys
      name,
      inputId,
      input: input2,
      isLeading,
      isTrailing,
      // eslint-disable-next-line vue/no-dupe-keys
      inputClass,
      leadingIconName,
      leadingIconClass,
      leadingWrapperIconClass,
      trailingIconName,
      trailingIconClass,
      trailingWrapperIconClass,
      onInput,
      onChange,
      onBlur
    };
  }
});
</script>
<template>
  <div class="min-h-screen flex items-center justify-center p-8">
    <div class="w-full max-w-md">
      <div class="bg-peach-100 rounded-3xl p-8 backdrop-blur-sm bg-white/20">
        <div class="text-center mb-8">
          <h1 class="text-2xl font-bold mb-2">紫喬問數❤靈運指掌</h1>
        </div>

        <form @submit.prevent="calculateLifePathNumber" class="space-y-6">
          <!-- 日期選擇器 -->
          <div class="relative">
            <UInput
              v-model="birthDate"
              type="date"
              placeholder="年/月/日"
              class="w-full py-3 px-4 bg-white rounded-lg text-lg"
              required
            />
          </div>

          <!-- 計算按鈕 -->
          <button
            type="submit"
            class="w-full bg-black text-white py-3 px-4 rounded-lg text-lg hover:bg-gray-800 transition-colors">
            開始計算
          </button>
        </form>

        <!-- 結果顯示 -->
        <div v-if="result" class="mt-8 space-y-4">
          <!-- 生命靈數結果 -->
          <div class="text-center">
            <h2 class="text-3xl font-bold mb-4">
              您的生命靈數是：{{ result.number }}
            </h2>
            <p class="mt-2 text-lg">
              {{ result.description }}
            </p>
          </div>

          <!-- 九宮格顯示 -->
          <div class="mt-8">
            <div class="grid grid-cols-3 gap-2">
              <template v-for="row in gridNumbers" :key="row.join('')">
                <template v-for="num in row" :key="num">
                  <div
                    v-if="num !== null"
                    class="relative w-20 h-20 border border-gray-300 flex items-center justify-center text-2xl font-bold"
                    :class="{
                      'border-pink-500': num === result.number,
                      'bg-white/30': true
                    }"
                  >
                    {{ num }}
                    <!-- 圓圈標記 -->
                    <div
                      v-if="getCircleCount(num) > 0"
                      class="absolute inset-0 flex items-center justify-center"
                    >
                      <div
                        v-for="index in getCircleCount(num)"
                        :key="index"
                        class="absolute border-2 border-current rounded-full"
                        :class="{
                          'w-12 h-12': index === 1,
                          'w-16 h-16': index === 2,
                          'w-20 h-20': index === 3
                        }"
                        :style="{ zIndex: 20 - index }"
                      ></div>
                    </div>
                  </div>
                  <div v-else class="w-20 h-20"></div>
                </template>
              </template>
            </div>
          </div>

          <!-- 特質展示 -->
          <div class="space-y-4 mt-8">
            <div v-for="(trait, index) in result.traits" :key="index"
              class="bg-white/30 backdrop-blur-sm rounded-lg p-4">
              <h3 class="font-semibold mb-2">{{ trait.title }}</h3>
              <p class="text-sm">
                {{ trait.description }}
              </p>
            </div>
          </div>

          <!-- 其他人眼中的你 -->
          <div v-if="fromOthersEyesDescription" class="mt-8">
            <h3 class="text-xl font-bold mb-4">其他人眼中的你</h3>
            <p class="text-sm bg-white/30 backdrop-blur-sm rounded-lg p-4">
              {{ fromOthersEyesDescription }}
            </p>
          </div>

          <!-- 三合數解析 -->
          <div v-if="threeCombinationDescription" class="mt-8">
            <h3 class="text-xl font-bold mb-4">三合數解析</h3>
            <p class="text-sm bg-white/30 backdrop-blur-sm rounded-lg p-4">
              {{ threeCombinationDescription }}
            </p>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script setup lang="ts">
interface Trait {
  title: string
  description: string
}

interface LifePathResult {
  number: number
  description: string
  traits: Trait[]
}

const birthDate = ref('')
const result = ref<LifePathResult | null>(null)
const fromOthersEyesDescription = ref<string>('')
const threeCombinationDescription = ref<string>('')

// 九宮格數據
const gridNumbers = [
  [1, 2, 3],
  [4, 5, 6],
  [7, 8, 9]
]

// 計算生命靈數
const calculateNumber = (num: number): number => {
  if (num < 10) return num
  return calculateNumber(
    String(num)
      .split('')
      .reduce((sum, digit) => sum + parseInt(digit), 0)
  )
}

// 計算在生日中出現的次數
const getCircleCount = (number: number): number => {
  if (!birthDate.value) return 0
  let count = 0
  const dateString = birthDate.value.replace(/-/g, '')
  dateString.split('').forEach((digit: string) => {
    if (parseInt(digit) === number) count++
  })
  return count
}

// 生命靈數描述
