  8: "在他人眼中是有野心、果斷且充滿影響力的人。您天生具備領導能力，擅長管理資源與追求成功，對目標有強大的執行力與決心。",
  9: "在他人眼中是充滿愛與智慧的理想主義者。您具有博愛精神，重視人類的共同福祉，經常為他人付出，展現無私的特質。"
}

// 三合數描述
const threeCombination: Record<string, string> = {
  "123": "具有創造力和表達能力，善於領導和溝通。",
  "456": "務實穩重，具有組織能力和責任感。",
  "789": "智慧深邃，追求真理和精神成長。",
  "147": "獨立自主，具有分析能力和創新思維。",
  "258": "善於協調，具有商業頭腦和管理才能。",
  "369": "富有藝術天賦，具有理想主義精神。"
}

const calculateLifePathNumber = () => {
  const date = new Date(birthDate.value)
  const year = date.getFullYear()
  const month = date.getMonth() + 1
  const day = date.getDate()

  const yearSum = calculateNumber(year)
  const monthSum = calculateNumber(month)
  const daySum = calculateNumber(day)
  const lifePathNumber = calculateNumber(yearSum + monthSum + daySum)

  result.value = lifePathDescriptions[lifePathNumber]
  fromOthersEyesDescription.value = fromOthersEyes[lifePathNumber] || ''

  // 計算三合數
  const numbers = [yearSum, monthSum, daySum].sort((a, b) => a - b)
  const combinationKey = numbers.join('')
  threeCombinationDescription.value = threeCombination[combinationKey] || ''
}
</script>

